    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        try {
            String sql="insert into Account(Phone_no,Username,Surname,Firstname,Address,Password,Sec_Q,Answer,Date) values(?,?,?,?,?,?,?,?,?)";
            if((surname.getText().length()<3) || (firstname.getText().length()<3) || 
                    (username.getText().length()<=3) || (password.getText().length()<=3) || (answer.getText().length()<3) || 
                    (phone.getText().length()<8) || (address.getText().length()<3) ){
                JOptionPane.showMessageDialog(null, "All the fields are required\n Please supply input. Thank you");
            }
            else {
                
            
            pst=conn.prepareStatement(sql);
                pst.setString(1,phone.getText());
                pst.setString(2,username.getText());
                pst.setString(3,surname.getText());
                pst.setString(4,firstname.getText());
                pst.setString(5,address.getText());
                pst.setString(6,password.getText());
                pst.setString(7,(String)jComboBox1.getSelectedItem());
                pst.setString(8,answer.getText());
               // String date =Integer.toString(getDate);
               String date=t.toString();
               pst.setString(9, date);
                pst.execute();
                JOptionPane.showMessageDialog(null, "Congratulation\n Your Account has been Successfully Created\n You can now Login");
                  pst.close();
                  this.setVisible(false);
                    Login ob=new Login();
                    ob.setVisible(true);
            }
        }
        catch(SQLException e) {
            JOptionPane.showMessageDialog(null, e.getMessage());
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

